if (msg.payload < 20) {
    msg.payload = 70;
} else msg.payload = 0;
    return msg;

let value = msg.payload
let last_value = flow.get("last_value") || value

if (last_value == value){
    node.send(last_value)
}
else{
    flow.set("last_value", value)
}

return ;

const FACTOR_VELOCIDAD = 50;
const FACTOR_DIRECCION = 50;

const direccion = msg.payload.direccion;
const adelante = msg.payload.adelante;
const atras = msg.payload.atras;
const rotarIzq = msg.payload.rotarIzq;
const rotarDer = msg.payload.rotarDer;

const izq = (direccion < 0) ? Math.abs(direccion) * FACTOR_DIRECCION : 0
const der = (direccion > 0) ? direccion * FACTOR_DIRECCION : 0


let izquierdaAdelante, izquierdaAtras, derechaAdelante, derechaAtras;

if (rotarIzq > 0) {
    izquierdaAdelante = FACTOR_VELOCIDAD;
    izquierdaAtras = 0;
    derechaAdelante = 0;
    derechaAtras = FACTOR_VELOCIDAD;
} else if (rotarDer > 0) {
    izquierdaAdelante = 0;
    izquierdaAtras = FACTOR_VELOCIDAD;
    derechaAdelante = FACTOR_VELOCIDAD;
    derechaAtras = 0;
} else if (adelante > 0) {
    izquierdaAdelante = adelante * FACTOR_VELOCIDAD + izq - der;
    izquierdaAtras = 0;
    derechaAdelante = adelante * FACTOR_VELOCIDAD + der - izq;
    derechaAtras = 0;
} else {
    izquierdaAdelante = 0;
    izquierdaAtras = atras * FACTOR_VELOCIDAD + izq - der;
    derechaAdelante = 0;
    derechaAtras = atras * FACTOR_VELOCIDAD + der - izq;
}

return [
    { payload: izquierdaAdelante },
    { payload: izquierdaAtras },
    { payload: derechaAdelante },
    { payload: derechaAtras },
    { payload: { direccion, adelante, atras, rotarIzq, rotarDer } }
]
